mapper.select:
select id, member_email, member_password, member_name, created_date, updated_date
from tbl_member
where member_email = #{memberEmail}
  and member_password = #{memberPassword}

select id, member_email, member_password, member_name, created_date, updated_date
from tbl_member
where member_email = #{memberEmail}

select count(*)
from tbl_member
where member_email = #{memberEmail}
